package java;import java.text.NumberFormat;/** * Created by liutizhong on 2016/3/31. */public class day01 {    public  String DataExchange(String data){        NumberFormat numberFormat1 = NumberFormat.getNumberInstance();        return numberFormat1.format(data);    }    public static  void main(String args){        day01 s=new day01();        s.DataExchange("100000.0");        System.out.println(s.DataExchange("100000.0"));    }}