package yougou.chapter1;/** * Created by liutizhong on 2015/12/11. * 选择排序 */public class SelectionSort {    static void SelectionSort_fum(int[] M) {        int key =0,minValue=0;        for (int i = 0; i < M.length; i++) {            minValue=M[i];            for (int j = M.length-1; j >= i; j--) {                if (M[j] <= minValue) {                    minValue=M[j];                    key=j;                }            }            M[key]=M[i];            M[i]=minValue;        }        for(int m=0;m<M.length;m++){            System.out.print("\t"+M[m]);        }    }    public static void main(String[] args) {        int[] M = {49, 38, 65, 97, 76, 13, 27, 49};        SelectionSort_fum(M);    }}